import Authenticated from "resources/Authenticated";

export default class MedicineResource extends Authenticated {
  id = null;
  name = "";
  price = "";
  factory = "";
  currStock = 0;
  minStock = 0;
  medsType = null;
  medsCategory = null;

  pk() {
    return this.id;
  }

  static minimalListShape() {
    return {
      ...this.listShape(),
      getFetchKey: () => {
        return "/api/medicines/minimal/";
      },
      fetch: (params: {}, body?: Readonly<object | string>) => {
        return this.fetch(
          "get",
          `${process.env.REACT_APP_API_HOST}/api/medicines/minimal/`,
          body
        );
      }
    };
  }

  static urlRoot = `${process.env.REACT_APP_API_HOST}/api/medicines/`;
}
