  payAmt = 0;
  taken = false;

  pk() {
    return this.id;
  }

  static payTansactionShape() {
    return {
      ...this.updateShape(),
      getFetchKey: () => {
        return "/api/transactions/pay/";
      },
      fetch: (params: {}, body?: Readonly<object | string>) => {
        return this.fetch(
          "put",
          `${process.env.REACT_APP_API_HOST}/api/transactions/pay/${params.id}`,
          body
        );
      }
    };
  }

